void Qsort(int a[],int low,int up)
{
    int i,j,k,x;
    if (low>=up) return;
    else
    {
        k=a[low];
        j=low;
        for (i=low;i<=up;i++)
        {
            if (a[i]>k)
            {
                j++;
                x=a[i];
                a[i]=a[j];
                a[j]=x;
            }
        }
        x=k;
        a[low]=a[j];
        a[j]=x;
        Qsort(a,low,j-1);
        Qsort(a,j+1,up);
        
    }}
