int adet(int a,int b,int c)
{
	int x=0;
	if (b>a)x=x+1;
	if (c==a)x=x+1;
	return (x);

}
int bdet(int a,int b,int c)
{
	int x=0;
	if (a>b)x=x+1;
	if (c<a)x=x+1;
	return (x);
}
int cdet(int a,int b,int c)
{
